(14, 'Ретробар Вакансии', '<p><span style="color: #0000ff;">Наши вакансии:</span></p>\r\n<p><span style="color: #0000ff;">Бармен 2 чел.</span></p>\r\n<p><span style="color: #0000ff;">Техслужащий 1 чел.</span></p>\r\n<p><span style="color: #0000ff;">Менеджер 1 чел.</span></p>', 'vacancy', 'Вакансии Ретробара', NULL, NULL, '2014-05-29 13:24:52', '2014-05-29 14:34:45', 'RETROBAR'),
(15, 'О Ретро-Баре', '<p><span style="color: #0000ff;">Наш Ретро-Бар открылся в ...</span></p>\r\n<p><span style="color: #0000ff;">Наш Ретро-Бар является лучшим Ретро-Баром в городе&nbsp;</span></p>', 'about', 'О Ретро-Баре', NULL, NULL, '2014-05-29 13:26:36', '2014-05-29 14:33:25', 'RETROBAR'),
